mapper.update:
update goods
set `state`=#{updatedState,jdbcType=TINYINT}
where id=#{id,jdbcType=BIGINT}
and org_id=#{orgId,jdbcType=BIGINT}
limit  1

update goods
set `state`=#{newState,jdbcType=TINYINT}
where org_id=#{orgId,jdbcType=BIGINT}
and `state`=#{oldState,jdbcType=TINYINT}

update goods
set delete_flag=#{updatedDeleteFlag,jdbcType=TINYINT}
where id=#{id,jdbcType=BIGINT}
and org_id=#{orgId,jdbcType=BIGINT}
limit 1
