mapper.select:
select

from task
where id in

    #{item,jdbcType=BIGINT}

and org_id=#{orgId,jdbcType=BIGINT}
order by id desc

select

from task
where parent_id=#{parentId,jdbcType=BIGINT}
and org_id=#{orgId,jdbcType=BIGINT}
order by id desc

select

from task
where org_id=#{request.orgId,jdbcType=BIGINT}

    and task_type = #{request.taskType,jdbcType=INTEGER}


    and staff_id = #{request.staffId,jdbcType=INTEGER}


    and state = #{request.state,jdbcType=INTEGER}


    and project_id = #{request.projectId,jdbcType=INTEGER}

order by id desc

select

from task
where org_id=#{request.orgId,jdbcType=BIGINT}
and staff_id = #{request.staffId,jdbcType=INTEGER}

    and task_type = #{request.taskType,jdbcType=INTEGER}


    and state = #{request.state,jdbcType=INTEGER}


    and project_id = #{request.projectId,jdbcType=INTEGER}

order by id desc

select

from task
where org_id=#{request.orgId,jdbcType=BIGINT}
and project_id = #{request.projectId,jdbcType=INTEGER}

    and task_type = #{request.taskType,jdbcType=INTEGER}


    and state = #{request.state,jdbcType=INTEGER}


    and staff_id = #{request.staffId,jdbcType=INTEGER}

order by id desc

mapper.update:
update task
set staff_id=#{updatedStaffId,jdbcType=BIGINT}
where id in

    #{item,jdbcType=BIGINT}

and org_id=#{orgId,jdbcType=BIGINT}

update task
set staff_id=#{updatedStaffId,jdbcType=BIGINT}
where id=#{id,jdbcType=BIGINT}
and org_id=#{orgId,jdbcType=BIGINT}
limit 1
