mapper.select:
select

from `order`
where related_trade_no=#{key.relatedTradeNo,jdbcType=VARCHAR}

    and state = #{key.state,jdbcType=TINYINT}


    and trade_type = #{key.tradeType,jdbcType=TINYINT}


    and buyer_id = #{key.buyerId,jdbcType=BIGINT}


    and seller_id = #{key.sellerId,jdbcType=BIGINT}


    and expired_at > #{key.expiredAt,jdbcType=BIGINT}
