mapper.update:
update account_wechat
set uid=#{updatedUid,jdbcType=BIGINT}
where open_id=#{openId,jdbcType=VARCHAR}

update account_wechat
set open_id=#{updatedOpenId,jdbcType=VARCHAR}
where uid=#{uid,jdbcType=BIGINT} and union_id=${unionId,jdbcType=VARCHAR} and delete_flag = 0

update account_wechat
set mp_open_id=#{updatedMpOpenId,jdbcType=VARCHAR}
where uid=#{uid,jdbcType=BIGINT} and union_id=${unionId,jdbcType=VARCHAR} and delete_flag = 0
